from pydantic_settings import BaseSettings, SettingsConfigDict
from pydantic import field_validator, Field
from typing import List, Optional, Union
import os


class Settings(BaseSettings):
    """Application settings"""
    
    model_config = SettingsConfigDict(
        env_file=".env",
        case_sensitive=False,
        extra="ignore"  # Ignore extra fields from environment
    )
    
    # Application
    app_name: str = "CubularPyAPI"
    app_version: str = "1.0.0"
    debug: bool = False
    log_level: str = "INFO"
    
    # Database - optimized for asyncpg direct connections
    database_host: str = Field(default="localhost", alias="DATABASE_HOST")
    database_port: int = Field(default=5432, alias="DATABASE_PORT")
    database_name: str = Field(default="projectxy", alias="DATABASE_NAME")
    database_user: str = Field(default="postgres", alias="DATABASE_USER")
    database_password: str = Field(default="boticas2025", alias="DATABASE_PASSWORD")
    
    # Lightweight database pool settings
    database_pool_size: int = 5      # Reduced from 20
    database_max_overflow: int = 5   # Reduced from 10
    
    @property
    def database_url(self) -> str:
        """Construct database URL from individual components"""
        return f"postgresql+asyncpg://{self.database_user}:{self.database_password}@{self.database_host}:{self.database_port}/{self.database_name}"
    
    # Security
    secret_key: str = Field(
        alias="JWT_SECRET",
        default="",
        description="Secret key for JWT"
    )
    encryption_secret: Optional[str] = Field(
        default=None,
        alias="ENCRYPTION_SECRET",
        description="Secret key for API key encryption/decryption"
    )
    jwt_algorithm: str = "HS256"
    access_token_expire_minutes: int = 1440  # 24 hours
    refresh_token_expire_days: int = 7
    
    # CORS
    allowed_origins: Union[str, List[str]] = Field(default="")
    
    @field_validator("allowed_origins", mode="before")
    @classmethod
    def assemble_cors_origins(cls, v):
        if v is None or v == "":
            return []
        if isinstance(v, str):
            return [i.strip() for i in v.split(",") if i.strip()]
        elif isinstance(v, list):
            return v
        return []
    
    # AI Services (API keys come from llm_settings table, not environment variables)
    # These are kept for backward compatibility but not used in production
    openai_api_key: Optional[str] = None
    anthropic_api_key: Optional[str] = None
    embedding_model: str = "text-embedding-ada-002"
    
    # Email/SMTP Configuration
    smtp_host: Optional[str] = None
    smtp_port: int = 587
    smtp_user: Optional[str] = None
    smtp_pass: Optional[str] = None
    smtp_from: Optional[str] = None
    smtp_tls: bool = True  # Use TLS (if False, uses SSL)
    
    # Legacy email settings (for backward compatibility)
    smtp_username: Optional[str] = None
    smtp_password: Optional[str] = None
    from_email: str = "noreply@projectxy.com"
    
    @property
    def SMTP_HOST(self) -> Optional[str]:
        return self.smtp_host
    
    @property
    def SMTP_PORT(self) -> int:
        return self.smtp_port
    
    @property
    def SMTP_USER(self) -> Optional[str]:
        return self.smtp_user or self.smtp_username
    
    @property
    def SMTP_PASS(self) -> Optional[str]:
        return self.smtp_pass or self.smtp_password
    
    @property
    def SMTP_FROM(self) -> Optional[str]:
        return self.smtp_from or self.from_email
    
    @property
    def SMTP_TLS(self) -> bool:
        return self.smtp_tls
    
    # File Upload
    max_file_size: int = 10485760  # 10MB
    upload_dir: str = "survey_data"  # Changed from "../survey_data" to be relative to project root
    
    # Vector Search (only for manual operations via API)
    default_similarity_threshold: float = 0.25
    max_search_results: int = 10000


# Create settings instance
settings = Settings()

# Ensure upload directory exists
os.makedirs(settings.upload_dir, exist_ok=True)